import axios from "axios";
import React, { useEffect, useState } from "react";
import ComboBox from "react-responsive-combo-box";
import 'react-responsive-combo-box/dist/index.css'
import '../Product.css'

function Product() {
    //--------------Model
    const [productCategory, setProductCategory] = useState({
        category:
            [
                'Clothing',
                'Computer',
                'Book'
            ]
    });

    const [selectedCategory, setSelectedCategory] = useState("Mai");

    //--------------Functions
    function populateCategory() {
        const saved_token = localStorage.getItem("jwt");
        const AuthStr = 'Bearer '.concat(saved_token);

        axios.get('http://localhost:8080/products/getAllCategory', { headers: { 'Authorization': AuthStr } })
            .then(res => {
                setProductCategory({ category: res.data })
            })
            .catch(error => {
                console.error('Error:', error);
            })
    }

    function clickMe() {

    }

    function onChangeCategory(event) {
        setSelectedCategory(event.target.value)
    }

    //--------------Operation
    useEffect(() => {
        localStorage.setItem("jwt", "eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJhZG1pbiIsImlhdCI6MTYzOTYwMzAxMiwiZXhwIjoxNjM5NjIxMDEyfQ.DrkHHpg-Zs2KuccnysMMihTobwASM_PhLpSMsi7sJnA");
        localStorage.setItem("user", "admin");

        populateCategory();
    }, [])

    //--------------View
    return (
        <div>
            <div className="productForm">
                <label>Create new Product:</label>
                <br />
                <label>Name:</label>
                <br />
                <input type="text"></input>
                <br />
                <br />
                <label>Price:</label>
                <br />
                <input type="text"></input>
                <br />
                <br />
                <ComboBox options={productCategory.category} name="category" enableAutocomplete onSelect={onChangeCategory}/>
                <input type="submit" onClick={clickMe}></input>
                <label>{selectedCategory}</label>
            </div>
        </div>
    )
}

export default Product;
