import axios from "axios";
import React, { useEffect, useState } from "react";
import ComboBox from "react-responsive-combo-box";
import 'react-responsive-combo-box/dist/index.css'
import '../Product.css'

function Product() {
    //--------------Model
    const [productCategory, setProductCategory] = useState({
        category: [
            'Clothing', 
            'Computer',
            'Book'
        ]
    });

    //--------------Functions
    function populateCategory() {
        localStorage.setItem("jwt", "eyJhbGciOiJIUzI1NiJ9.eyJzdWIiOiJjdXN0b21lcjEiLCJpYXQiOjE2Mzk1ODM2NTUsImV4cCI6MTYzOTYwMTY1NX0.pUH_io-KBbfBmkUjU7PFGtM-ywACMIkwRO3bIdq5Vhg");
        localStorage.setItem("user", "customer1");

        axios.get('http://localhost:8080/products/getAllCategory')
        .then(res => {
            setProductCategory({ category: res.data })
        })
        .catch(error => {
            alert(error, localStorage.getItem("jwt"));
        })
    }

    //--------------Operation
    useEffect(() => {
        populateCategory();
    })

    //--------------View
    return(
        <div>
            <div className="productForm">
                <label>Create new Product:</label>
                <br/>
                <label>Name:</label>
                <br/>
                <input type="text"></input>
                <br/>
                <br/>
                <label>Price:</label>
                <br/>
                <input type="text"></input>
                <br/>
                <br/>
                <ComboBox options={productCategory.category} enableAutocomplete onChange={populateCategory}/>
            </div>
        </div>
    )
}

export default Product;
